{
    "name": "ltcdocs",  
    "fields": [
        {"name": "content", "type": "Edm.String", "key": false, "searchable": true, "retrievable": false, "filterable": false, "sortable": false, "facetable": false},
        {"name": "metadata_storage_path", "type": "Edm.String", "key": true, "searchable": true, "retrievable": true },
        {"name": "locations", "type": "Collection(Edm.String)", "key": false, "searchable": true, "retrievable": true },
        {"name": "lonlats", "type": "Collection(Edm.GeographyPoint)", "key": false, "searchable": false, "retrievable": true }
    ]
}

{   
    "name" : "cognitiveskillsindexer",  
    "description" : "Uses builtin cognitive skills and the custom ones.",  
    "dataSourceName" : "ltcblobstore",
    "targetIndexName" : "ltcdocs",  
    "skillsetName" : "smartskillset",
    "parameters" : { "configuration" : { "imageAction" : "generateNormalizedImages", "dataToExtract" : "contentAndMetadata" }},
    "fieldMappings" : [ { "sourceFieldName" : "metadata_storage_path", "targetFieldName" : "metadata_storage_path" , "mappingFunction" :{ "name" : "base64Encode" } } ],
    "outputFieldMappings": [ {"sourceFieldName": "/document/locations", "targetFieldName":"locations"}, {"sourceFieldName": "/document/lat-lon/*", "targetFieldName":"lonlats"}
    ]
}

{
  "name" : "smartskillset",
  "description": "Document analysis skill set.",
  "skills":
  [
  	{
        "@odata.type": "#Microsoft.Skills.Custom.WebApiSkill",
        "description": "A custom Geo Enhancer skill to extract lon-lat from locations via Azure Maps services.",
        "uri": "https://msftltc.azurewebsites.net/api/GeoEnricher?code=utjM3epxrRbLZGD0puIQiRb4GQUy5DkVzidcE8IJeZRAMbSVm6Xs3Q==",
        "batchSize":1,
        "context": "/document",
        "inputs": [
          {
            "name": "locations",
            "source": "/document/locations"
          }
        ],
        "outputs": [
          {
            "name": "lon-lat",
            "targetName": "lon-lat"
          }
        ]
      },
    {
      "@odata.type": "#Microsoft.Skills.Text.NamedEntityRecognitionSkill",
      "context": "/document",
      "categories": [ "Location" ],
      "defaultLanguageCode": "en",
      "minimumPrecision": 0.5,
      "inputs": [
        {
          "name": "text",
          "source": "/document/merged_content"
        }
      ],
      "outputs": [
        {
          "name": "locations",
          "targetName": "locations"
        }
      ]
    },
       {
            "@odata.type": "#Microsoft.Skills.Text.MergeSkill",
            "description": null,
            "context": "/document",
            "inputs": [
                {
                    "name": "text",
                    "source": "/document/content"
                },
                {
                    "name": "itemsToInsert",
                    "source": "/document/normalized_images/*/text"
                },
                {
                    "name": "offsets",
                    "source": "/document/normalized_images/*/contentOffset"
                }
            ],
            "outputs": [
                {
                    "name": "mergedText",
                    "targetName": "merged_content"
                }
            ],
            "insertPreTag": " ",
            "insertPostTag": " "
        },
	{
	    "description": "Extracts text (plain and structured) from image.",
	    "@odata.type": "#Microsoft.Skills.Vision.OcrSkill",
	    "context": "/document/normalized_images/*",
	    "defaultLanguageCode": null,
	    "detectOrientation": true,
	    "inputs": [
	      {
	        "name": "image",
	        "source": "/document/normalized_images/*"
	      }
	    ],
	    "outputs": [
	      {
	        "name": "text",
	        "targetName": "text"
	      },
	      {
	        "name": "layoutText",
	        "targetName": "layoutText"
	      }
	    ]
	}
  ]
}
